// Initial state and all of our actions will go here, such as fetching data etc.

import React, { useReducer } from 'react';
import axios from 'axios';
import GithubContext from './githubContext';
import GithubReducer from './githubReducer';
import {
    SEARCH_USERS,
    SET_LOADING,
    CLEAR_USERS,
    GET_USER,
    GET_REPOS
} from '../types';

const GithubState = props => {
    const initialState = {
        users: [],
        user: {},
        repos: [],
        loading: false
    };

    const [state, dispatch] = useReducer(GithubReducer, initialState);

    // Search User
    //▓▓ Search GitHub users (passed into the Search component as a prop)
    const searchUsers = async search => {
        setLoading();

        const res = await axios.get(
            `https://api.github.com/search/users?q=${search}&client_id=${process.env.REACT_APP_GITHUB_CLIENT_ID}&client_secret=${process.env.REACT_APP_GITHUB_CLIENT_SECRET}`
        );
        console.log(res.data.items);

        dispatch({
            type: SEARCH_USERS,
            payload: res.data.items
        });
    };

    // Get User
    // ▓▓ Get a single GitHub user (? marks FIRST parameter of the search query)
    const getUser = async username => {
        setLoading();

        const res = await axios.get(
            `https://api.github.com/users/${username}?client_id=${process.env.REACT_APP_GITHUB_CLIENT_ID}&client_secret=${process.env.REACT_APP_GITHUB_CLIENT_SECRET}`
        );
        console.log(res.data);

        dispatch({ type: GET_USER, payload: res.data });
    };

    // Get Repos

    // Clear Users
    //▓▓ Clear users from state (passed into search component as a prop)
    const clearUsers = () => dispatch({ type: CLEAR_USERS });

    // Set Loading
    const setLoading = () => dispatch({ type: SET_LOADING });

    return (
        <GithubContext.Provider
            value={{
                // EVERYTHING WE NEED AVAILABE MUST BE HERE
                users: state.users,
                user: state.user,
                repos: state.repos,
                loading: state.loading,
                searchUsers,
                clearUsers,
                getUser,
                getUserRepos
            }}
        >
            {props.children}
        </GithubContext.Provider>
    );
};

export default GithubState;
